-- Script de Backup Integral do Banco de Dados Supabase - VERSÃO COMPLETA E LIMPA
-- Este script realiza um backup completo preservando a estrutura da tabela e todos os dados
-- Versão: 1.0 (29/04/2025)
-- Sem duplicações - Pronto para importação

-------------------------------------------------------
-- BLOCO 1: Criação da tabela e limpeza de dados existentes
-------------------------------------------------------
BEGIN;
-- Cria a tabela se ela não existir
CREATE TABLE IF NOT EXISTS public.registrations (
    id uuid NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,
    nome_completo text NOT NULL,
    cpf text NOT NULL,
    cep text NOT NULL,
    endereco text NOT NULL,
    numero text NOT NULL,
    bairro text NOT NULL,
    cidade text NOT NULL,
    estado text NOT NULL,
    estado_civil text NOT NULL,
    nome_contato text NOT NULL,
    whatsapp_contato text NOT NULL,
    created_at timestamptz DEFAULT now(),
    verified boolean DEFAULT false,
    sequence_number integer NOT NULL,
    payment_method text
);

-- Limpa a tabela completamente antes de inserir novos registros
TRUNCATE TABLE public.registrations RESTART IDENTITY CASCADE;
COMMIT;

-------------------------------------------------------
-- BLOCO 2: Inserção dos dados (deduplicated)
-------------------------------------------------------
BEGIN;


COMMIT;

-------------------------------------------------------
-- BLOCO 3: Verificação de integridade
-------------------------------------------------------
BEGIN;
DO $$
DECLARE
    count_original integer := 81; -- Número de registros no banco original
    count_new integer;
BEGIN
    -- Verifica se a tabela existe
    IF NOT EXISTS (
        SELECT 1 FROM information_schema.tables 
        WHERE table_schema = 'public' AND table_name = 'registrations'
    ) THEN
        RAISE EXCEPTION 'Tabela registrations não existe após o backup';
    END IF;
    
    -- Verifica se todos os registros foram importados
    SELECT COUNT(*) INTO count_new FROM public.registrations;
    
    IF count_new != count_original THEN
        RAISE WARNING 'Número de registros diverge: Banco original tinha % registros, backup tem % registros', 
                      count_original, count_new;
    ELSE
        RAISE NOTICE 'Verificação de integridade concluída com sucesso. Todos os % registros foram preservados.', count_new;
    END IF;
END
$$;
COMMIT;
